package com.miramicodigo.textinputlayout;import android.support.design.widget.TextInputEditText;import android.support.design.widget.TextInputLayout;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.text.Editable;import android.text.TextWatcher;public class MainActivity extends AppCompatActivity {    private TextInputLayout errorInputLayout, customErrorInputLayout;    private TextInputEditText errorEditText, customErrorEditText;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        errorEditText = (TextInputEditText) findViewById(R.id.tietUno);        errorInputLayout = (TextInputLayout) findViewById(R.id.tilUno);        errorEditText.addTextChangedListener(new TextWatcher() {            @Override            public void beforeTextChanged(CharSequence charSequence, int i, int i1, int i2) {            }            @Override            public void onTextChanged(CharSequence charSequence, int i, int i1, int i2) {            }            @Override            public void afterTextChanged(Editable editable) {                if(editable.length() > errorInputLayout.getCounterMaxLength()) {                    errorInputLayout.setError("Cantidad de caracteres maximo es: "+                            errorInputLayout.getCounterMaxLength());                } else{                    errorInputLayout.setError(null);                }            }        });    }}